style.css.content:
/* ===== CSS RESET & BASE STYLES ===== */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: 'Space Grotesk', sans-serif;
    background-color: #000000;
    color: #ffffff;
    line-height: 1.6;
    overflow-x: hidden;
}

/* ===== CONTAINER & LAYOUT ===== */
.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

/* ===== HEADER & NAVIGATION ===== */
.header {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
    z-index: 1000;
    transition: all 0.3s ease;
}

.nav-container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 2rem;
    max-width: 1200px;
    margin: 0 auto;
}

.logo-container {
    display: flex;
    align-items: center;
}

.logo {
    height: 40px;
    width: 40px;
    border-radius: 50%;
    object-fit: cover;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 2rem;
    align-items: center;
}

.nav-link {
    color: #ffffff;
    text-decoration: none;
    font-weight: 400;
    font-size: 0.95rem;
    transition: all 0.3s ease;
    position: relative;
}

.nav-link:hover,
.nav-link.active {
    color: #00cc6a;
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: #00cc6a;
    transition: width 0.3s ease;
}

.nav-link:hover::after,
.nav-link.active::after {
    width: 100%;
}

/* ===== HERO SECTION WITH FLOATING ELEMENTS ===== */
.main-content {
    margin-top: 80px;
}

.hero-section {
    min-height: 100vh;
    display: grid;
    grid-template-columns: 1fr 2fr 1fr;
    align-items: center;
    gap: 2rem;
    position: relative;
    background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
    overflow: hidden;
    padding: 2rem;
}

/* Hero Content */
.hero-content {
    grid-column: 2;
    text-align: left;
    z-index: 10;
    position: relative;
    max-width: 600px;
    justify-self: start;
    padding: 2rem 0;
}

.hero-title {
    font-size: clamp(2.5rem, 6vw, 4.5rem);
    font-weight: 700;
    margin-bottom: 1.5rem;
    line-height: 1.2;
}

.hero-accent {
    color: #00cc6a;
    position: relative;
}

.hero-description {
    font-size: 1.2rem;
    color: #cccccc;
    margin-bottom: 2.5rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.hero-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

.cta-button {
    padding: 1rem 2rem;
    border: none;
    border-radius: 50px;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 500;
    font-size: 1rem;
    cursor: pointer;
    transition: all 0.3s ease;
    text-decoration: none;
    display: inline-block;
}

.cta-button.primary {
    background: #00cc6a;
    color: #ffffff;
}

.cta-button.primary:hover {
    background: #00994d;
    transform: translateY(-2px) scale(1.1);
    box-shadow: 0 10px 30px rgba(0, 204, 106, 0.3);
}

.cta-button.secondary {
    background: transparent;
    color: #ffffff;
    border: 2px solid rgba(255, 255, 255, 0.3);
}

.cta-button.secondary:hover {
    border-color: #00cc6a;
    color: #00cc6a;
    transform: translateY(-2px) scale(1.1);
}

/* ===== FLOATING ELEMENTS ===== */
.floating-element {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 1.5rem;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
    animation: float 6s ease-in-out infinite;
    z-index: 5;
    position: relative;
    transition: all 0.3s ease;
    cursor: pointer;
}

.floating-element:hover {
    transform: scale(1.08) translateY(-10px);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.5);
    border-color: rgba(0, 204, 106, 0.3);
    animation-play-state: paused;
}

@keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(1deg); }
}

/* Left Column Floating Elements */
.hero-left {
    grid-column: 1;
    display: flex;
    flex-direction: column;
    gap: 2rem;
    align-items: center;
    justify-content: center;
}

/* Stats Card */
.stats-card {
    width: 280px;
    animation-delay: 0s;
}

.stats-header {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1rem;
}

.stats-avatar {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background: linear-gradient(135deg, #00cc6a, #00994d);
}

.stats-info h4 {
    font-size: 0.95rem;
    font-weight: 600;
    margin-bottom: 0.25rem;
}

.stats-info p {
    font-size: 0.8rem;
    color: #aaaaaa;
}

.stats-chart {
    height: 60px;
    position: relative;
    overflow: hidden;
}

.chart-line {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 40px;
    background: linear-gradient(to right, 
        transparent, 
        rgba(0, 204, 106, 0.3) 25%, 
        rgba(0, 204, 106, 0.6) 50%, 
        rgba(0, 204, 106, 0.4) 75%, 
        transparent
    );
    border-radius: 4px 4px 0 0;
}

/* Right Column Floating Elements */
.hero-right {
    grid-column: 3;
    display: flex;
    flex-direction: column;
    gap: 2rem;
    align-items: center;
    justify-content: center;
}

/* Project Card */
.project-card {
    width: 250px;
    animation-delay: 1s;
}

.project-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.project-header h4 {
    font-size: 0.95rem;
    font-weight: 600;
}

.project-status {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #00cc6a;
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.5; }
}

.project-list {
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
}

.project-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    font-size: 0.85rem;
}

.project-dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
}

.project-dot.web { background: #00cc6a; }
.project-dot.trading { background: #00cc6a; }
.project-dot.respect { background: #8b5cf6; }

/* Tech Card */
.tech-card {
    width: 200px;
    animation-delay: 2s;
}

.tech-card h4 {
    font-size: 0.95rem;
    font-weight: 600;
    margin-bottom: 1rem;
}

.tech-icons {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 0.75rem;
}

.tech-icon {
    background: rgba(0, 204, 106, 0.1);
    border: 1px solid rgba(0, 204, 106, 0.3);
    border-radius: 8px;
    padding: 0.5rem;
    text-align: center;
    font-size: 0.75rem;
    font-weight: 600;
    color: #00cc6a;
}

/* Performance Card */
.performance-card {
    width: 220px;
    animation-delay: 3s;
}

.performance-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
}

.performance-header h4 {
    font-size: 0.95rem;
    font-weight: 600;
}

.performance-indicator {
    font-size: 1.2rem;
    color: #00cc6a;
}

.performance-metrics {
    display: flex;
    justify-content: space-between;
}

.metric {
    text-align: center;
}

.metric-value {
    display: block;
    font-size: 1.5rem;
    font-weight: 700;
    color: #00cc6a;
}

.metric-label {
    font-size: 0.75rem;
    color: #aaaaaa;
}

/* ===== ABOUT SECTION ===== */
.about-section {
    padding: 8rem 0;
    background: linear-gradient(180deg, #000000 0%, #1a1a1a 50%, #000000 100%);
}

.about-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: center;
}

.about-title {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 2rem;
    color: #ffffff;
}

.about-description {
    color: #cccccc;
    line-height: 1.8;
    margin-bottom: 1.5rem;
    font-size: 1.1rem;
}

.about-description strong {
    color: #00cc6a;
}

.about-stats {
    display: flex;
    gap: 2rem;
    margin: 2.5rem 0;
    flex-wrap: wrap;
}

.about-stat {
    text-align: center;
}

.about-stat .stat-number {
    display: block;
    font-size: 2rem;
    font-weight: 700;
    color: #00cc6a;
    margin-bottom: 0.5rem;
}

.about-stat .stat-label {
    color: #aaaaaa;
    font-size: 0.9rem;
}

.about-cta {
    display: inline-block;
    background: #00cc6a;
    color: #ffffff;
    text-decoration: none;
    padding: 1rem 2rem;
    border-radius: 50px;
    font-weight: 500;
    transition: all 0.3s ease;
    margin-top: 1rem;
}

.about-cta:hover {
    background: #00994d;
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(0, 204, 106, 0.3);
}

.about-image {
    display: flex;
    justify-content: center;
    align-items: center;
}

.image-placeholder {
    border-radius: 20px;
    overflow: hidden;
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.image-placeholder svg {
    width: 100%;
    height: auto;
    max-width: 400px;
}

/* ===== RESPONSIVE HERO SECTION ===== */
@media (max-width: 1024px) {
    .hero-section {
        grid-template-columns: 0.8fr 1.4fr 0.8fr;
        gap: 1.5rem;
        padding: 2rem 1rem;
    }

    .floating-element {
        width: 100% !important;
        max-width: 220px;
        padding: 1rem;
    }

    .stats-card {
        max-width: 200px;
    }

    .project-card {
        max-width: 180px;
    }

    .tech-card {
        max-width: 160px;
    }

    .performance-card {
        max-width: 180px;
    }
}

@media (max-width: 768px) {
    .hero-section {
        grid-template-columns: 1fr;
        grid-template-rows: auto auto auto;
        gap: 2rem;
        min-height: auto;
        padding: 3rem 1rem;
    }

    .hero-left {
        grid-column: 1;
        grid-row: 1;
        order: 1;
        flex-direction: row;
        justify-content: space-around;
        gap: 1rem;
    }

    .hero-content {
        grid-column: 1;
        grid-row: 2;
        order: 2;
        text-align: center;
        justify-self: center;
        max-width: 100%;
    }

    .hero-right {
        grid-column: 1;
        grid-row: 3;
        order: 3;
        flex-direction: row;
        justify-content: space-around;
        gap: 1rem;
    }

    .floating-element {
        width: 100% !important;
        max-width: 280px;
        margin: 0 auto;
    }

    .hero-title {
        font-size: clamp(2rem, 8vw, 3rem);
    }

    .hero-buttons {
        flex-direction: column;
        align-items: center;
        gap: 1rem;
    }

    .cta-button {
        width: 100%;
        max-width: 250px;
    }

    .about-content {
        grid-template-columns: 1fr;
        gap: 3rem;
        text-align: center;
    }

    .about-image {
        order: -1;
    }

    .about-stats {
        justify-content: center;
    }
}

/* ===== BURGER MENU ===== */
.burger-menu {
    display: none;
    flex-direction: column;
    justify-content: space-around;
    width: 30px;
    height: 30px;
    background: transparent;
    border: none;
    cursor: pointer;
    z-index: 1001;
}

.burger-line {
    width: 100%;
    height: 3px;
    background: #ffffff;
    border-radius: 2px;
    transition: all 0.3s ease;
}

.burger-menu.active .burger-line:nth-child(1) {
    transform: rotate(45deg) translate(8px, 8px);
}

.burger-menu.active .burger-line:nth-child(2) {
    opacity: 0;
}

.burger-menu.active .burger-line:nth-child(3) {
    transform: rotate(-45deg) translate(8px, -8px);
}

/* ===== PILLARS SECTION ===== */
.pillars-section {
    padding: 8rem 0;
    background: linear-gradient(180deg, #1a1a1a 0%, #000000 100%);
}

.section-title {
    font-size: 2.5rem;
    font-weight: 700;
    text-align: center;
    margin-bottom: 4rem;
    color: #ffffff;
}

.pillars-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 2.5rem;
    align-items: start;
}

.pillar-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 2.5rem;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.pillar-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #00cc6a, #00994d);
    border-radius: 20px 20px 0 0;
}

.pillar-card:hover {
    transform: translateY(-10px) scale(1.05);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.4);
    border-color: rgba(255, 107, 53, 0.3);
}

.pillar-image {
    width: 100%;
    height: 120px;
    margin-bottom: 1.5rem;
    border-radius: 15px;
    overflow: hidden;
    background: rgba(255, 255, 255, 0.02);
    border: 1px solid rgba(255, 255, 255, 0.05);
}

.pillar-image svg {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.pillar-icon {
    width: 80px;
    height: 80px;
    background: rgba(255, 107, 53, 0.1);
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    color: #00cc6a;
}

.pillar-title {
    font-size: 1.5rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.pillar-description {
    color: #cccccc;
    margin-bottom: 1.5rem;
    line-height: 1.7;
}

.pillar-features {
    list-style: none;
    margin-bottom: 2rem;
}

.pillar-features li {
    color: #aaaaaa;
    margin-bottom: 0.5rem;
    position: relative;
    padding-left: 1.5rem;
}

.pillar-features li::before {
    content: '✓';
    position: absolute;
    left: 0;
    color: #00cc6a;
    font-weight: bold;
}

.pillar-cta {
    display: inline-block;
    background: #00cc6a;
    color: #ffffff;
    text-decoration: none;
    padding: 0.75rem 1.5rem;
    border-radius: 50px;
    font-weight: 500;
    transition: all 0.3s ease;
}

.pillar-cta:hover {
    background: #00994d;
    transform: translateY(-2px) scale(1.1);
    box-shadow: 0 10px 20px rgba(0, 204, 106, 0.3);
}

/* ===== PAGE HEADERS ===== */
.page-header {
    padding: 8rem 0 4rem;
    text-align: center;
    background: linear-gradient(135deg, #1a1a1a 0%, #000000 100%);
    position: relative;
}

.page-header-content {
    position: relative;
    z-index: 2;
}

.page-icon {
    width: 120px;
    height: 120px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 2rem;
}

.page-icon img {
    width: 120px;
    height: 120px;
    object-fit: contain;
}

.page-title {
    font-size: 3rem;
    font-weight: 700;
    margin-bottom: 1rem;
    color: #ffffff;
}

.page-subtitle {
    font-size: 1.2rem;
    color: #cccccc;
    max-width: 600px;
    margin: 0 auto;
}

/* ===== PAGE CONTENT ===== */
.page-content {
    padding: 4rem 0;
}

.content-section {
    margin-bottom: 6rem;
}

.content-section h2 {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 2rem;
    color: #ffffff;
}

.lead-text {
    font-size: 1.1rem;
    color: #cccccc;
    line-height: 1.8;
    margin-bottom: 3rem;
}

/* ===== SERVICES GRID ===== */
.services-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}

.service-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    padding: 2rem;
    transition: all 0.3s ease;
}

.service-card:hover {
    transform: translateY(-5px) scale(1.05);
    border-color: rgba(255, 107, 53, 0.3);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.service-icon {
    font-size: 2.5rem;
    margin-bottom: 1rem;
}

.service-card h3 {
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.service-card p {
    color: #cccccc;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.service-features {
    list-style: none;
}

.service-features li {
    color: #aaaaaa;
    margin-bottom: 0.5rem;
    position: relative;
    padding-left: 1.5rem;
    font-size: 0.9rem;
}

.service-features li::before {
    content: '→';
    position: absolute;
    left: 0;
    color: #00cc6a;
}

/* ===== TECHNOLOGY TAGS ===== */
.tech-categories {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.tech-category h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.tech-tags {
    display: flex;
    flex-wrap: wrap;
    gap: 0.75rem;
}

.tech-tag {
    background: rgba(0, 204, 106, 0.1);
    border: 1px solid rgba(0, 204, 106, 0.3);
    color: #00cc6a;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 500;
}

/* ===== APPROACH & PROCESS ===== */
.approach-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-top: 2rem;
}

.approach-item {
    text-align: center;
    padding: 1.5rem;
}

.approach-item h3 {
    font-size: 1.2rem;
    margin-bottom: 1rem;
    color: #ffffff;
}

.approach-item p {
    color: #cccccc;
    line-height: 1.6;
}

.process-timeline {
    display: grid;
    gap: 2rem;
}

.process-step {
    display: flex;
    align-items: flex-start;
    gap: 2rem;
    padding: 1.5rem;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.05);
}

.step-number {
    width: 50px;
    height: 50px;
    background: #00cc6a;
    color: #ffffff;
    font-weight: 700;
    font-size: 1.2rem;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.step-content h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #ffffff;
}

.step-content p {
    color: #cccccc;
    line-height: 1.6;
}

/* ===== TRADING SPECIFIC STYLES ===== */
.trading-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 2rem;
    margin: 3rem 0;
}

.stat-item {
    text-align: center;
    padding: 1.5rem;
    background: rgba(255, 255, 255, 0.03);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: #00cc6a;
    display: block;
    margin-bottom: 0.5rem;
}

.stat-label {
    color: #cccccc;
    font-size: 0.9rem;
}

.strategies-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
}

.strategy-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    padding: 1.5rem;
    transition: all 0.3s ease;
}

.strategy-card:hover {
    border-color: rgba(255, 107, 53, 0.3);
    transform: translateY(-3px) scale(1.03);
}

.strategy-card h3 {
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.strategy-card p {
    color: #cccccc;
    font-size: 0.9rem;
    margin-bottom: 1rem;
    line-height: 1.6;
}

.strategy-metrics {
    display: flex;
    gap: 1rem;
    flex-wrap: wrap;
}

.metric {
    background: rgba(0, 204, 106, 0.1);
    color: #00cc6a;
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 500;
    border: 1px solid rgba(0, 204, 106, 0.3);
}

/* Risk Management */
.risk-management {
    display: grid;
    gap: 2rem;
}

.risk-item {
    display: flex;
    align-items: flex-start;
    gap: 1.5rem;
    padding: 2rem;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.05);
}

.risk-icon {
    font-size: 2rem;
    flex-shrink: 0;
}

.risk-content h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #ffffff;
}

.risk-content p {
    color: #cccccc;
    line-height: 1.6;
}

/* ===== RESPECT PAGE SPECIFIC STYLES ===== */
.philosophy-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin: 3rem 0;
}

.philosophy-item {
    text-align: center;
    padding: 2rem;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.05);
}

.philosophy-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
}

.philosophy-item h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.philosophy-item p {
    color: #cccccc;
    line-height: 1.6;
}

.values-list {
    display: grid;
    gap: 3rem;
}

.value-item {
    padding: 2.5rem;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 20px;
    border: 1px solid rgba(255, 255, 255, 0.05);
}

.value-header {
    display: flex;
    align-items: center;
    gap: 2rem;
    margin-bottom: 1.5rem;
}

.value-number {
    width: 60px;
    height: 60px;
    background: linear-gradient(135deg, #00cc6a, #00994d);
    color: #ffffff;
    font-weight: 700;
    font-size: 1.5rem;
    border-radius: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
}

.value-header h3 {
    font-size: 1.5rem;
    font-weight: 600;
    color: #ffffff;
}

.value-item p {
    color: #cccccc;
    line-height: 1.7;
    margin-bottom: 1.5rem;
}

.value-details {
    list-style: none;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 0.75rem;
}

.value-details li {
    color: #aaaaaa;
    position: relative;
    padding-left: 1.5rem;
    font-size: 0.9rem;
}

.value-details li::before {
    content: '✓';
    position: absolute;
    left: 0;
    color: #00cc6a;
    font-size: 0.7rem;
}

.commitments-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}

.commitment-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 15px;
    padding: 2rem;
    text-align: center;
    transition: all 0.3s ease;
    position: relative;
}

.commitment-card:hover {
    transform: translateY(-5px) scale(1.05);
    border-color: rgba(255, 107, 53, 0.3);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.commitment-icon {
    font-size: 2.5rem;
    margin-bottom: 1rem;
}

.commitment-card h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.commitment-card p {
    color: #cccccc;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.commitment-badge {
    background: #00cc6a;
    color: #ffffff;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    display: inline-block;
}

.methodology-timeline {
    display: grid;
    gap: 2rem;
}

.methodology-step {
    display: flex;
    align-items: flex-start;
    gap: 2rem;
    padding: 2rem;
    background: rgba(255, 255, 255, 0.02);
    border-radius: 15px;
    border: 1px solid rgba(255, 255, 255, 0.05);
    transition: all 0.3s ease;
}

.methodology-step:hover {
    border-color: rgba(255, 107, 53, 0.2);
    transform: translateX(10px) scale(1.02);
}

.step-icon {
    font-size: 2rem;
    flex-shrink: 0;
}

/* ===== CTA SECTIONS ===== */
.cta-section {
    background: linear-gradient(135deg, rgba(255, 107, 53, 0.1) 0%, rgba(255, 143, 90, 0.05) 100%);
    border: 1px solid rgba(255, 107, 53, 0.2);
    border-radius: 20px;
    padding: 4rem 2rem;
    text-align: center;
    margin: 4rem 0;
}

.cta-content h2 {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    color: #ffffff;
}

.cta-content p {
    color: #cccccc;
    font-size: 1.1rem;
    margin-bottom: 2rem;
    max-width: 600px;
    margin-left: auto;
    margin-right: auto;
}

.cta-buttons {
    display: flex;
    gap: 1rem;
    justify-content: center;
    flex-wrap: wrap;
}

.btn-primary,
.btn-secondary {
    padding: 1rem 2rem;
    border-radius: 50px;
    text-decoration: none;
    font-family: 'Space Grotesk', sans-serif;
    font-weight: 500;
    font-size: 1rem;
    transition: all 0.3s ease;
    display: inline-block;
}

.btn-primary {
    background: #00cc6a;
    color: #ffffff;
    border: 2px solid #00cc6a;
}

.btn-primary:hover {
    background: #00994d;
    border-color: #00994d;
    transform: translateY(-2px) scale(1.1);
    box-shadow: 0 10px 30px rgba(0, 204, 106, 0.3);
}

.btn-secondary {
    background: transparent;
    color: #ffffff;
    border: 2px solid rgba(255, 255, 255, 0.3);
}

.btn-secondary:hover {
    border-color: #00cc6a;
    color: #00cc6a;
    transform: translateY(-2px) scale(1.1);
}

/* ===== FOOTER ===== */
.footer {
    background: #0a0a0a;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    padding: 4rem 0 2rem;
}

.footer-content {
    display: grid;
    grid-template-columns: 1fr 2fr;
    gap: 4rem;
    margin-bottom: 2rem;
}

.footer-brand {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.footer-logo {
    height: 50px;
    width: 50px;
    border-radius: 50%;
    object-fit: cover;
}

.footer-tagline {
    color: #aaaaaa;
    font-style: italic;
}

.footer-links {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 2rem;
}

.footer-column h4 {
    color: #ffffff;
    font-weight: 600;
    margin-bottom: 1rem;
}

.footer-column ul {
    list-style: none;
}

.footer-column li {
    margin-bottom: 0.5rem;
}

.footer-column a {
    color: #aaaaaa;
    text-decoration: none;
    transition: color 0.3s ease;
}

.footer-column a:hover {
    color: #00cc6a;
}

.footer-bottom {
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    text-align: center;
    color: #666666;
}

/* ===== TEAM SECTION ===== */
.team-section {
    padding: 8rem 0;
    background: linear-gradient(135deg, #1a1a1a 0%, #000000 100%);
}

.team-intro {
    text-align: center;
    font-size: 1.2rem;
    color: #cccccc;
    margin-bottom: 4rem;
    margin-top: -2rem;
}

.team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2.5rem;
    align-items: start;
}

.team-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 2.5rem;
    text-align: center;
    transition: all 0.3s ease;
    position: relative;
    overflow: hidden;
}

.team-card::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #00cc6a, #00994d);
    border-radius: 20px 20px 0 0;
}

.team-card:hover {
    transform: translateY(-10px) scale(1.05);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.4);
    border-color: rgba(255, 107, 53, 0.3);
}

.team-avatar {
    margin-bottom: 1.5rem;
    display: flex;
    justify-content: center;
}

.avatar-placeholder {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    overflow: hidden;
    background: rgba(255, 255, 255, 0.05);
    border: 2px solid rgba(255, 255, 255, 0.1);
}

.team-name {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #00cc6a;
}

.team-role {
    font-size: 1rem;
    color: #00cc6a;
    font-weight: 500;
    margin-bottom: 1rem;
}

.team-description {
    color: #cccccc;
    line-height: 1.6;
    margin-bottom: 1.5rem;
    font-size: 0.95rem;
}

.team-skills {
    display: flex;
    flex-wrap: wrap;
    gap: 0.5rem;
    justify-content: center;
}

.skill-tag {
    background: rgba(0, 204, 106, 0.1);
    border: 1px solid rgba(0, 204, 106, 0.3);
    color: #00cc6a;
    padding: 0.25rem 0.75rem;
    border-radius: 15px;
    font-size: 0.8rem;
    font-weight: 500;
}

/* Mobile Responsiveness for Team Section */
@media (max-width: 768px) {
    .team-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .team-card {
        padding: 2rem;
    }

    .team-intro {
        font-size: 1.1rem;
        margin-bottom: 3rem;
    }
}

/* ===== CONTACT SECTION ===== */
.contact-section {
    padding: 8rem 0;
    background: linear-gradient(180deg, #000000 0%, #1a1a1a 100%);
}

.contact-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: start;
}

.contact-title {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 1.5rem;
    color: #ffffff;
}

.contact-description {
    font-size: 1.1rem;
    color: #cccccc;
    line-height: 1.7;
    margin-bottom: 3rem;
}

.contact-details {
    display: flex;
    flex-direction: column;
    gap: 2rem;
}

.contact-item {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.contact-icon {
    font-size: 1.5rem;
    width: 50px;
    height: 50px;
    background: rgba(255, 107, 53, 0.1);
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.contact-item h4 {
    font-size: 1.1rem;
    font-weight: 600;
    color: #ffffff;
    margin-bottom: 0.3rem;
}

.contact-item p {
    color: #cccccc;
    font-size: 0.95rem;
}

.contact-form-wrapper {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 20px;
    padding: 3rem;
    backdrop-filter: blur(10px);
}

.contact-form {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.form-group {
    display: flex;
    flex-direction: column;
}

.form-group label {
    font-size: 0.9rem;
    font-weight: 500;
    color: #ffffff;
    margin-bottom: 0.5rem;
}

.form-group input,
.form-group select,
.form-group textarea {
    background: rgba(255, 255, 255, 0.05);
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 10px;
    padding: 1rem;
    color: #ffffff;
    font-size: 0.95rem;
    transition: all 0.3s ease;
}

.form-group input:focus,
.form-group select:focus,
.form-group textarea:focus {
    outline: none;
    border-color: #00cc6a;
    box-shadow: 0 0 0 3px rgba(0, 204, 106, 0.1);
}

.form-group input::placeholder,
.form-group textarea::placeholder {
    color: #888888;
}

.checkbox-group {
    flex-direction: row;
    align-items: center;
    gap: 0.5rem;
}

.checkbox-label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.9rem;
    color: #cccccc;
    cursor: pointer;
}

.checkbox-label input[type="checkbox"] {
    appearance: none;
    width: 20px;
    height: 20px;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-radius: 4px;
    position: relative;
    cursor: pointer;
}

.checkbox-label input[type="checkbox"]:checked {
    background: #00cc6a;
    border-color: #00cc6a;
}

.checkbox-label input[type="checkbox"]:checked::after {
    content: '✓';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    font-size: 12px;
    font-weight: bold;
}

.submit-btn {
    background: linear-gradient(135deg, #00cc6a, #00994d);
    border: none;
    border-radius: 12px;
    padding: 1rem 2rem;
    color: #ffffff;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
    margin-top: 1rem;
}

.submit-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(0, 204, 106, 0.3);
}

.submit-btn:active {
    transform: translateY(0);
}

/* ===== RESPONSIVE DESIGN ===== */
@media (max-width: 768px) {
    .burger-menu {
        display: flex;
    }

    .nav-menu {
        position: fixed;
        top: 80px;
        left: -100%;
        width: 100%;
        height: calc(100vh - 80px);
        background: rgba(0, 0, 0, 0.98);
        backdrop-filter: blur(20px);
        flex-direction: column;
        justify-content: flex-start;
        align-items: center;
        gap: 2rem;
        padding: 3rem 0;
        transition: left 0.3s ease;
        z-index: 1000;
    }

    .nav-menu.active {
        left: 0;
    }

    .nav-menu li {
        width: 80%;
        text-align: center;
    }

    .nav-link {
        font-size: 1.2rem;
        padding: 1rem;
        display: block;
        border-radius: 10px;
        transition: all 0.3s ease;
    }

    .nav-link:hover {
        background: rgba(255, 107, 53, 0.1);
    }

    .contact-content {
        grid-template-columns: 1fr;
        gap: 3rem;
    }

    .contact-form-wrapper {
        padding: 2rem;
    }
    .floating-elements {
        position: static;
        margin: 2rem 0;
        display: flex;
        flex-direction: column;
        gap: 1.5rem;
        align-items: center;
    }

    .floating-element {
        position: static;
        transform: none;
        animation: none;
        width: 100%;
        max-width: 280px;
        margin: 0;
    }

    .hero-content {
        padding: 1rem;
    }

    .hero-buttons {
        flex-direction: column;
        align-items: center;
    }

    .cta-button {
        width: 100%;
        max-width: 300px;
    }

    .pillars-grid {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .pillar-card {
        padding: 2rem;
    }

    .container {
        padding: 0 1rem;
    }

    .page-title {
        font-size: 2rem;
    }

    .section-title {
        font-size: 2rem;
    }

    .services-grid,
    .commitments-grid {
        grid-template-columns: 1fr;
    }

    .tech-categories {
        grid-template-columns: 1fr;
    }

    .process-step,
    .methodology-step,
    .risk-item {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .value-header {
        flex-direction: column;
        text-align: center;
        gap: 1rem;
    }

    .footer-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .cta-buttons {
        flex-direction: column;
        align-items: center;
    }

    .btn-primary,
    .btn-secondary {
        width: 100%;
        max-width: 300px;
    }
}

@media (max-width: 480px) {
    .hero-title {
        font-size: 2rem;
    }

    .hero-description {
        font-size: 1rem;
    }

    .page-header {
        padding: 6rem 0 3rem;
    }

    .page-icon {
        width: 80px;
        height: 80px;
    }

    .pillar-card,
    .service-card,
    .commitment-card {
        padding: 1.5rem;
    }

    .cta-section {
        padding: 3rem 1rem;
        margin: 3rem 0;
    }
}

/* ===== ACCESSIBILITY & PERFORMANCE ===== */
@media (prefers-reduced-motion: reduce) {
    .floating-element,
    .project-status {
        animation: none;
    }

    * {
        transition: none !important;
    }
}

/* Focus states for keyboard navigation */
.nav-link:focus,
.cta-button:focus,
.pillar-cta:focus,
.btn-primary:focus,
.btn-secondary:focus {
    outline: 2px solid #00cc6a;
    outline-offset: 2px;
}

/* High contrast mode support */
@media (prefers-contrast: high) {
    .floating-element {
        border-color: rgba(255, 255, 255, 0.5);
        background: rgba(0, 0, 0, 0.8);
    }

    .pillar-card,
    .service-card,
    .commitment-card {
        border-color: rgba(255, 255, 255, 0.3);
    }
}

/* ===== AI PAGE SPECIFIC STYLES ===== */
.ai-header {
    background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
}

.ai-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
    margin: 3rem 0;
}

.ai-stats .stat-item {
    text-align: center;
    padding: 2rem;
    background: rgba(0, 204, 106, 0.05);
    border: 1px solid rgba(0, 204, 106, 0.2);
    border-radius: 15px;
    transition: all 0.3s ease;
}

.ai-stats .stat-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 30px rgba(0, 204, 106, 0.2);
    border-color: rgba(0, 204, 106, 0.4);
}

.ai-stats .stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    color: #00cc6a;
    display: block;
    margin-bottom: 0.5rem;
}

.ai-stats .stat-label {
    color: #cccccc;
    font-size: 1rem;
    font-weight: 500;
}

/* AI Neural Network Animation */
@keyframes neural-pulse {
    0%, 100% { 
        opacity: 0.4; 
        transform: scale(1);
    }
    50% { 
        opacity: 1; 
        transform: scale(1.1);
    }
}

.ai-pillar .pillar-image svg circle {
    animation: neural-pulse 3s infinite;
}

.ai-pillar .pillar-image svg circle:nth-child(odd) {
    animation-delay: 0.5s;
}

.ai-pillar .pillar-image svg circle:nth-child(even) {
    animation-delay: 1s;
}

/* AI Service Cards Enhancement */
.service-card[data-ai="true"] {
    border: 1px solid rgba(0, 204, 106, 0.3);
    background: linear-gradient(135deg, rgba(0, 204, 106, 0.05) 0%, rgba(0, 0, 0, 0.1) 100%);
}

.service-card[data-ai="true"]:hover {
    border-color: rgba(0, 204, 106, 0.6);
    box-shadow: 0 15px 40px rgba(0, 204, 106, 0.2);
}

/* AI Ethics Section */
.ai-ethics {
    background: rgba(0, 204, 106, 0.02);
    border: 1px solid rgba(0, 204, 106, 0.1);
    border-radius: 20px;
    padding: 3rem;
    margin: 3rem 0;
}

.ai-ethics h2 {
    color: #00cc6a;
    margin-bottom: 2rem;
}

/* Responsive AI Stats */
@media (max-width: 768px) {
    .ai-stats {
        grid-template-columns: repeat(2, 1fr);
        gap: 1rem;
    }

    .ai-stats .stat-item {
        padding: 1.5rem;
    }

    .ai-stats .stat-number {
        font-size: 2rem;
    }
}

@media (max-width: 480px) {
    .ai-stats {
        grid-template-columns: 1fr;
    }
}

