/* ===== ANIMATIONS POUR LE TEXTE RESPECT ===== */

/* Animation 1: Typewriter Effect */
@keyframes typewriter {
    0% {
        width: 0;
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        width: 100%;
        opacity: 1;
    }
}

@keyframes blink-caret {
    from, to { border-color: transparent; }
    50% { border-color: #00cc6a; }
}

/* Animation 2: Fade In avec Slide Up */
@keyframes fadeInSlideUp {
    0% {
        opacity: 0;
        transform: translateY(30px);
    }
    100% {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Animation 3: Glitch Effect */
@keyframes glitch {
    0% {
        transform: translate(0);
    }
    20% {
        transform: translate(-2px, 2px);
    }
    40% {
        transform: translate(-2px, -2px);
    }
    60% {
        transform: translate(2px, 2px);
    }
    80% {
        transform: translate(2px, -2px);
    }
    100% {
        transform: translate(0);
    }
}

/* Animation 4: Gradient Text Animation */
@keyframes gradientShift {
    0% {
        background-position: 0% 50%;
    }
    50% {
        background-position: 100% 50%;
    }
    100% {
        background-position: 0% 50%;
    }
}

/* Animation 5: Neon Glow */
@keyframes neonGlow {
    0%, 100% {
        text-shadow: 
            0 0 5px #00cc6a,
            0 0 10px #00cc6a,
            0 0 15px #00cc6a,
            0 0 20px #00cc6a;
    }
    50% {
        text-shadow: 
            0 0 10px #00cc6a,
            0 0 20px #00cc6a,
            0 0 30px #00cc6a,
            0 0 40px #00cc6a;
    }
}

/* Animation 6: Matrix Rain Effect */
@keyframes matrixRain {
    0% {
        background-position: 0% 0%;
    }
    100% {
        background-position: 0% 100%;
    }
}

